import { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
 const API = "https://api.mymemory.translated.net/get";

 const [translate, setTranslate] = useState(''); // Corrected state declaration
 const [translated, setTranslated] = useState(''); // Corrected state declaration

 const {lang, setLang} = useState('');
 const {toLang, setToLang} = useState('');


 const translateOptions = [
  { value: '', label: 'option 1'},
  { value: '', label: 'option 2'},
  { value: '', label: 'option 3'},
  { value: '', label: 'option 4'},

 ]

 const translateOptionChange = (event) => {
  setLang(event.target.value)
 }



 const translateText = async () => {
  try {
    const response = await axios.get(API, {
      params: {
        q: translate,
        langpair: 'en|es', // Replace with your desired language pair (e.g., 'en|fr' for English to French)
      },
    });

    if (response.data && response.data.responseData) {
      const translated = response.data.responseData.translatedText;
      setTranslated(translated);
    } else {
      setTranslated('Translation not found');
    }
  } catch (error) {
    console.error('Error translating text:', error);
    setTranslated('An error occurred');
  }
};

  return (
    <div className="App">
      <header className="App-header">
        <div className='mainTranslatorBox'>
            <div className='translator'>
              <div className='toTranslate'>
              <button onClick={translateText}>Translate</button>
                <h2>Enter your text here:</h2>
                <input className='textInput' 
                    type="text"
                    placeholder="Enter text to translate"
                    value={translate}
                    onChange={(e) => setTranslate(e.target.value)}/>
              </div>

              <div className='translated'>
                  <h2>Translated:</h2>
                  <p>{translated}</p>
              </div>
            </div>
            <div className='ImageHere'>

            </div>
        </div>
      </header>
    </div>
  );
}

export default App;
